import React, { useState } from 'react';
import { ref, get, child } from 'firebase/database';  // Firebase Database functions
import { database } from './firebase';  // Import the initialized database instance

function App() {
  const [data, setData] = useState(null);  // Will store the data fetched from Firebase
  const [email, setEmail] = useState('');  // Will store the user's input
  const [error, setError] = useState('');  // Will store any error messages

  // Function to convert the email into Firebase-friendly format
  const formatEmailForFirebase = (email) => {
    return email.replace(/@/g, '-at-').replace(/\./g, '-dot-');
  };

  // Function to fetch data when the form is submitted
  const fetchData = async (e) => {
    e.preventDefault();  // Prevent page refresh on form submission
    setError('');  // Reset error message

    try {
      const formattedEmail = formatEmailForFirebase(email);  // Convert email to Firebase-friendly format
      const dbRef = ref(database);
      const snapshot = await get(child(dbRef, `/${formattedEmail}`));  // Query the database

      if (snapshot.exists()) {
        const firebaseData = snapshot.val();
        setData(Object.keys(firebaseData).map(key => ({
          id: key,
          ...firebaseData[key]
        })));
      } else {
        setData(null);  // Clear previous data
        setError('User does not exist');  // Set error message if no data found
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Something went wrong');  // Generic error message
    }
  };

  return (
    <div className="App">
      <h1>Check User Data</h1>

      {/* Form for inputting email */}
      <form onSubmit={fetchData}>
        <label>
          Enter email:
          <input
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}  // Update the email state on input change
            placeholder="Enter email address"
            required
          />
        </label>
        <button type="submit">Fetch Data</button>
      </form>

      {/* Display error if user doesn't exist */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Display the fetched data */}
      {data && (
        <div>
          <h2>Data for {email}:</h2>
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
