import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ref, get, child } from 'firebase/database'; // import firebase instance
import { database } from './firebase';

function App() {
  const [data, setData] = useState([]);
  // const [key, setKey] = useState('example-key');  // Set default key here
  const [email, setEmail] = useState('jknee-at-bu-dot-edu'); 

useEffect(() => {
  const fetchData = async () => {
    try {
      const dbRef = ref(database);
      console.log('Fetching data for:', email);  // Check if this logs the email correctly
      const snapshot = await get(child(dbRef, `/${email}`));  // Check if data is being fetched from the correct path
      if (snapshot.exists()) {
        const firebaseData = snapshot.val();
        console.log('Raw data from Firebase:', firebaseData);  // Log the raw data fetched
        const formattedData = Object.keys(firebaseData).map(key => ({
          id: key,
          ...firebaseData[key]
        }));
        console.log('Formatted data:', formattedData);  // Log the formatted data
        setData(formattedData);  // Store the array of data
      } else {
        console.log('No data available for this path:', `/${email}`);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  fetchData();
}, [email]);

  return (
    <div className="App">
      <h1>Data from Firebase for {email}:</h1>
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  );
}

export default App;
